package JavaBasics;

public class ForAndWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1;i<10;i++) {
			System.out.println(i);
		}
		int i=1;
		System.out.println("*********");
while(i<10) {
	System.out.println(i);
	i++;
	
}
	}

}
