package JavaBasics;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int i=1;
	int j= i++;// i=2,j=1
	System.out.println(i);
	System.out.println(j);
	
	int k=i--;//k=2,i=1
	int l=++i;//l=2,i=2
	int m=--i;//m=1,i=1
	System.out.println(k);
		System.out.println(l);
		System.out.println(m);
		
		
		
		
		
		
		
		
	}

}
